class Evil_Avr{
	public static function avr(array $params,$filename){
	    $zip = new clsTbsZip();
 	    $default=array('investorName'=>'','customerName'=>'','contractorName'=>'','constructionName'=>'','objectName'=>'',
		'investorCode'=>'','customerCode'=>'','contractorCode'=>'','constructionCode'=>'','objectCode'=>'',
		'kindOfActivity'=>'','contractNumber'=>'','typeOfOperation'=>'',

		'dayOfContract'=>'','mounthOfContract'=>'','yearOfContract'=>'',

		'documentNumber'=>'','dateOfDocument'=>'','reportingPeriodFrom'=>'','reportingPeriodTo'=>'','outlayPrice'=>'',

		'totalQuantity'=>'',
		'totalPrice'=>'',

		'table'=>array(),
		'personWhoGiveTranscript'=>'','personWhoGiveOfficialCapacity'=>'',
		'personWhoTakeTranscript'=>'','personWhoTakeOfficialCapacity'=>''
	    );
	    $params=array_merge($default,$params);
	    while(sizeof($params['table'])<10)
		$params['table'][]=array('byOrder'=>'','byOutlay'=>'','workName'=>'','unitPriceNumber'=>'','measurement'=>'','quantity'=>'','unitPrice'=>'','price'=>'');
	    $zip->Open(dirname(__FILE__).'/Avr/origin.odt');$zip->FileReplace('content.xml', Evil_Avr::_avrDocument($params), TBSZIP_STRING);
	    $zip->Flush(TBSZIP_FILE, $filename);
	    $zip->Close();
	    return true;
	}
